import React, {useState, useRef} from "react";
import './App.css';
import ListItem from "./ListItem";


const SecondList = () => {

  const [lists, setLists] = useState([]);

  const [listsEnd, setListsEnd] = useState([]);

  // const [lists, setLists] = useState([]);

  const ulRef = useRef(null)

  const addNewList = (event) => {
    event.preventDefault();
    const newList = prompt();

    setLists((lists) => [...lists, newList]);
  }

  const addInTheEnd = (event) => {
    event.preventDefault();
    const newList = prompt();
    

    setListsEnd((listsEnd) => [...listsEnd, newList]);
  }

  return (
    <div className="list-container-two">
      <ul ref={ulRef}>
        {Array.isArray(lists) && lists.map((list, index) => <ListItem key={index} listText={list} />)}
        <li key={5}>One</li>
        <li key={6}>Two </li>
        <li key={7}>Three </li>
        <li key={8}>Four </li>
        {Array.isArray(listsEnd) && lists.map((list, index) => <ListItem key={index} listText={list} />)}
      </ul>
      <button onClick={addNewList}>BEGINNING</button>
      <button onClick={addInTheEnd}>END</button>
      <button onClick={addNewList}>MIDDLE</button>
    </div>
  )
}

export default SecondList;
