import React, { useState, useRef} from "react";
import './App.css';
import ListItem from "./ListItem";

const List = () => {

  const liElement = useRef(null);
  const onButtonClick = () => {
    alert(liElement.current);
  }

  const alertClick = (text) => {
    alert(text);
  }

  return (
    <div className="list-container">
      <ul>
        <li key={1}>One <button onClick={() => alertClick('One') }>ALERT</button></li>
        <li key={2}>Two <button onClick={() => alertClick('Two') }>ALERT</button></li>
        <li key={3}>Three <button onClick={() => alertClick('Three') }>ALERT</button></li>
        <li key={4}>Four <button onClick={() => alertClick('Four') }>ALERT</button></li>
        <li key={10} ref={liElement}>Four <button onClick={onButtonClick}>ALERT</button></li>
      </ul>
    </div>
  );
}

const SecondList = () => {

  const [lists, setLists] = useState([]);

  const addNewList = () => {
    const newElement = prompt();

    setLists((lists) => [...lists, newElement]);
  }

  return(
    <div className="list-container-two">
      <ul>
        <li key={5}>One</li>
        <li key={6}>Two </li>
        <li key={7}>Three </li>
        <li key={8}>Four </li>
        <button onClick={handleClick}>CLICK</button>
      </ul>
    </div>
  )
}

export default List;
